import { gql } from '@apollo/client';

export default function getCategory(props)
{ return gql`
    query ExampleQuery {
  category(input: { title: "${props}" })  {
    name 
    products {
      id
      name
      inStock
      gallery
      description
      category
      attributes {
        id
        name
        type
        items {
          displayValue
          value
          id
        }
      }
      prices {
        currency {
          symbol
          label
        }
        amount
      }
      brand
    }
  }
}
 `;
}
