import { PureComponent} from "react";
import styled from "styled-components";
import icon from "./button-icon-empty-cart.png";

const ProductCardStyle = styled.div`

flex: 1;
order: 0;
flex-grow: 0;
background: #FFFFFF;
padding: 16px;
width: 386px;
position: relative;
&:hover {
box-shadow: 0px 4px 35px rgba(168, 172, 176, 0.19);
}

`;
const Button = styled.button`
display: none;
background: #5ECE7B;
width: 52px;
height: 52px;
border: none;
border-radius: 26px;
justify-content: center;

${ProductCardStyle}:hover & {
  display: block;
  position: absolute;
  top: 320px;
  left: 300px;
  cursor: pointer;
}
`;
const Image = styled.img`
width: 354px;
height: 330px;
object-fit: cover;
`;

const Title = styled.p`
font-weight: 300;
font-size: 18px;
line-height: 160%;
text-align: left;
color: #1D1F22;
margin: 24px 0 0 0;
`;
const Price = styled.p`
margin: 0;
font-weight: 500;
font-size: 18px;
line-height: 160%;
text-align: left;
color: #1D1F22;
`;
class ProductCard extends PureComponent{
constructor(props) {
        super(props);
        
        this.state = {
            product: this.props.product,
      };
      
    }

    render() {
        const { product } = this.state;
        return (
            <ProductCardStyle inS key={product.id}>
                <Image src={product.gallery[0]} alt=" product" />
                <Button> <img src={icon} alt="icon" /></Button>
                <Title>{product.brand} {product.name}</Title>
                <Price>{product.prices[0].currency.symbol } {product.prices[0].amount }</Price>
            </ProductCardStyle>
        )
    }

}

export default ProductCard;
