import { PureComponent} from "react";
import "./ProductList.css";
import categoryQuery from "../other-components-and-functions/categoryQuery";
import ProductCard from "../components/productCard/ProductCard";



class ClothesPage extends PureComponent {
    constructor(props) {
        super(props);
        
        this.state = {
        products: [],
        loading: false,
        error: null,
        };
    }
    
    async componentDidMount() {
      this.setState({ loading: true });
      let productsRequest = await categoryQuery("clothes");
      this.setState({ products: productsRequest, loading: false });
        }
   
render() {
   const { products, loading, error } = this.state;
    if (error) {
      return <p>Something went wrong</p>;
    }

    if (loading) {
      return <p>Loading ...</p>;
    }
    if(products !== null) {
      return (
      <>
        <div className="product-list-header">Clothes</div>
      <ContainerProductList>
            {products.map(product =>
              <ProductCard product={product} key={product.id} />
        )}
          </ContainerProductList>
      </>
    );}else {      
      return "Loading...";
    }
    }
};

export default ClothesPage;
