import { PureComponent} from "react";
import styled from "styled-components";
// import icon from "../images/button-icon-empty-cart.png";
import categoryQuery from "../other-components-and-functions/categoryQuery";
import ProductCard from "../components/productCard/ProductCard";
const Header = styled.div`
  font-size: 42px;
  font-weight: 400;
  line-height: 160%;
  color: #1D1F22;
  margin-left: 100px;
  text-align: left;
`;
const ContainerProductList = styled.div`
  display: flex;
  flex-wrap: wrap;
  flex-direction: row;
  margin: 100px 100px 190px 100px;
  align-items: center;
  row-gap: 100px;
  column-gap: 40px;

`;
const ProductCardStyle = styled.div`

flex: 1;
order: 0;
flex-grow: 0;
background: #FFFFFF;
padding: 16px;
width: 386px;
position: relative;
/* min-width: 380px; */
/* height: 444px; */
&:hover {
box-shadow: 0px 4px 35px rgba(168, 172, 176, 0.19);
}

`;
const Button = styled.button`
display: none;
background: #5ECE7B;
width: 52px;
height: 52px;
border: none;
border-radius: 26px;
justify-content: center;

${ProductCardStyle}:hover & {
  display: block;
  position: absolute;
  top: 320px;
  left: 300px;
  cursor: pointer;
}
`;
const Image = styled.img`
width: 354px;
height: 330px;
object-fit: cover;
`;

const Title = styled.p`
font-weight: 300;
font-size: 18px;
line-height: 160%;
text-align: left;
color: #1D1F22;
margin: 24px 0 0 0;
`;
const Price = styled.p`
margin: 0;
font-weight: 500;
font-size: 18px;
line-height: 160%;
text-align: left;
color: #1D1F22;
`;

class AllCategories extends PureComponent {
    constructor(props) {
        super(props);
        
        this.state = {
        products: null,
        loading: false,
        error: null,
      };
      
    }

     
    async componentDidMount() {
      this.setState({ loading: true });
      let productsRequest = await categoryQuery("all");
      this.setState({ products: productsRequest, loading: false });
        }
           
render() {
  const { products, loading, error } = this.state;
 
    if (error) {
      return <p>Something went wrong</p>;
    }

    if (loading) {
      return <p>Loading ...</p>;
    }
    if(products !== null)
    {
      return (
      <>
      <Header>All products</Header>
      <ContainerProductList>
            {products.map(product =>
              <ProductCard product={product} key={product.id} />
          // <ProductCardStyle key={product.id}>
          //   <Image src={product.gallery[0]} alt=" product" />
          //   <Button> <img src={icon} alt="icon" /></Button>
          //   <Title>{product.brand} {product.name}</Title>
          //   <Price>{product.prices[0].currency.symbol } {product.prices[0].amount }</Price>
          // </ProductCardStyle>
        )}
          </ContainerProductList>
          </>
    );
    } else {      
      return "Loading...";
    }
    }
};

export default AllCategories;
