import { PureComponent } from "react";
import { useQuery, gql } from '@apollo/client';

 const categoriesFullInfo = gql`query Query {
  categories {
    name
    products {
      id
      name
      inStock
      gallery
      description
      category
      attributes {
        id
        name
        type
        items {
          displayValue
          value
          id
        }
      }
      prices {
        currency {
          label
          symbol
        }
        amount
      }
      brand
    }
  }
  currencies {
    label
    symbol
  }
}
`;
function DisplayAllCategories() {
  const { loading, error, data } = useQuery(categoriesFullInfo);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Unfortunately, something is wrong with database. Please try later...</p>;
  return data.categories["all"].map(({ id, name, description, gallery }) => (
    <div key={id}>
      <h3>{name}</h3>
      <img width="400" height="250" alt="location-reference" src={`${gallery[0]}`} />
      <br />
      <b>About this location:</b>
      <p>{description}</p>
      <br />
    </div>
  ));
}
class AllCategories extends PureComponent {
   
    
    render() {
        return "Hellloooooo all categories";
        
    }
};

export default AllCategories;
