import { PureComponent } from "react";
import { connect } from 'react-redux';
import plus from "../images/plus-square.png";
import minus from "../images/minus-square.png";

import "./Cart.css";
import "./ProductPage.css";
import Slider from "../other-components-and-functions/Slider";

class Cart extends PureComponent {
    constructor(props) {
        super(props);
        this.state = {
            totalAmount: [],
            products: this.props.cartProducts,
            cartQuantity: this.props.cartQuantity,
            selectedAttributesArray: this.props.selectedAttributesArray,
        };
         this.getCurrentPrice = this.getCurrentPrice.bind(this);
    }
    getCurrentPrice(props) { 
        let current;
        props.map((item) => {
            if (item.currency.symbol === this.props.currency) {
                current = item.amount;
                return current;
            } else { return null; }
        })
        this.state.totalAmount.push(current);
        return current;
    }
    render() {
        const { products, selectedAttributesArray, cartQuantity } = this.state;
        if(cartQuantity !== 0)
        {
            return (
            <>
            <div className="cart-header">CART</div>          
            {products.map((product, index)=>{
                return (
                    <div className="cart-product-block" key={`${index} product`}>
                        <div className="part-1" key={`${index} product part 1`}>
                            <div className="cart-brand" key={`${product.brand}_${index}`}>{product.brand }</div>
                            <div className="cart-name" key={`${product.name}_${index}`}>{product.name}</div>
                            <div className="cart-price" key={`price_${index}`}>{ this.props.currency}{this.getCurrentPrice(product.prices)}</div>
                            {/* <p className="attribute-name"key={index}>{selectedAttributesArray[0][0][0] }:</p> */}
                            <div className="cart-item-container">
                                 <div className="attribute-item-selected"><p>S</p></div>
                                <div className="attribute-item"><p>M</p></div>
                            </div>
                        </div>
                        <div className="part-2" key={`${index} product part 2`}>
                            <div className="btn-wrapper">
                                <img className="btn-image" src={plus} alt="logo" />
                                <p className="counter" onClick={()=>{}}>1</p>
                                <img className="btn-image" src={minus} alt="logo" /> 
                            </div>
                             <div className="slider-container" >
                                <Slider data={product.gallery} />
                                {/* <img className="image-in-slider" src={products[0].gallery[0]} alt="logo"/> */}
                            </div>
                        </div>
                    </div>
                        )
                })}
                    
                    
                
                <div className="order-description">
                    <div className="order-column-1">
                        <p className="order-text">Tax 21%:</p>
                        <p className="order-text">Quantity:</p>
                        <p className="order-text">Total:</p>
                    </div>
                    <div className="order-column-2">
                     <p className="order-quantity">$40</p>
                     <p className="order-quantity">3</p>
                     <p className="order-quantity">$240</p>
                    </div>
                </div>
                <button type="submit" className="order-btn"><p> order</p></button>
                    
            </>
            )
        } else {
           return  <p>Your cart is empty...</p>
        }
    }
}
function mapStateToProps(state) {
    return {
        currency: state.currency.currency,
        cartProducts: state.cart.products,
        cartQuantity: state.cart.quantity,
        selectedAttributesArray: state.cart.selectedAttributesArray,
        
    }
}
export default connect(mapStateToProps)(Cart);
