import { PureComponent} from "react";




class TechPage extends PureComponent {
    constructor(props) {
        super(props);
        
        this.state = {
        data: [],
        loading: false,
        error: null,
        };
    }
    
 
    
    componentDidMount() {
        this.setState({ loading: true });
        const client = new ApolloClient({
  uri: 'http://localhost:4000/',
  cache: new InMemoryCache(),
        });
        const GET_ALL = gql`
        query Query {
  category(input: { title: "tech" }) {
    name
    
    products {
      id
      
      name
      inStock
      gallery
      description
      category
      attributes {
        id
        
        name
        type
        items {
          displayValue
          
          value
          id
        }
      }
      prices {
        currency {
          symbol
          
          label
        }
        amount
        
      }
      brand
    }
  }
  currencies {
    label
    
    symbol
  }
} `;
        client.query({ query: GET_ALL }).then((result) => {
            console.log(result);
            this.setState({ data: result.data.category.products, loading: false });
            
        },
            (error) => {
            this.setState({loading:true,error:error})
        });

        }
        
    
render() {
   const { data, loading, error } = this.state;
    if (error) {
      return <p>Something went wrong</p>;
    }

    if (loading) {
      return <p>Loading ...</p>;
    }

    return (
      <ul>
        {data.map(product =>
            <li key={product.id}>
                <h4>{product.name}</h4>
                
                <img src={product.gallery[0]} alt=" product" />
                
          </li>
        )}
      </ul>
    );
    }
};

export default TechPage;
