import { PureComponent} from "react";
import { gql, ApolloClient, InMemoryCache } from '@apollo/client';
import styled from "styled-components";

const Header = styled.div`
  font-size: 42px;
  font-weight: 400;
  line-height: 160%;
  color: #1D1F22;
  margin-left: 100px;
  text-align: left;
`;
const ContainerProductList = styled.div`
  display: flex;
  flex-wrap: wrap;
  /* flex-direction: row; */
  margin: 100px 100px 190px 100px;
 
  align-items: center;
  /* justify-content: space-between;  */
  row-gap: 100px;
  column-gap: 40px;

`;
const ProductCardStyle = styled.div`
/* display: flex;
flex-direction: column;
justify-content: flex-end;
align-items: center;
padding: 16px;

width: 386px;
height: 444px; */
flex: 1;
order: 0;
flex-grow: 0;
background: #FFFFFF;

min-width: 380px;
height: 444px;

`;

const Image = styled.img`
width: 354px;
height: 330px;
object-fit: cover;
`;

class AllCategories extends PureComponent {
    constructor(props) {
        super(props);
        
        this.state = {
        data: [],
        loading: false,
        error: null,
        };
    }
    fetchAll = () => {
        const client = new ApolloClient({
  uri: 'http://localhost:4000/',
  cache: new InMemoryCache(),
        });
        const GET_ALL = gql`
        query Query {
  category(input: { title: "all" }) {
    name
    
    products {
      id
      
      name
      inStock
      gallery
      description
      category
      attributes {
        id
        
        name
        type
        items {
          displayValue
          
          value
          id
        }
      }
      prices {
        currency {
          symbol
          
          label
        }
        amount
        
      }
      brand
    }
  }
  currencies {
    label
    
    symbol
  }
} `;
        client.query({ query: GET_ALL }).then((result) => {
            console.log(result);
            this.setState({ data: result.data.category.products, loading: false });
            },
            (error) => {
            this.setState({loading:true,error:error})
        });
    }
 
    
    componentDidMount() {
        this.setState({ loading: true });
        this.fetchAll();
        }
        
    
render() {
   const { data, loading, error } = this.state;
    if (error) {
      return <p>Something went wrong</p>;
    }

    if (loading) {
      return <p>Loading ...</p>;
    }
    if(data !== [])
    {
      return (
      <>
      <Header>All products</Header>
      <ContainerProductList>
        {data.map(product =>
            <ProductCardStyle key={product.id}>
                <h4>{product.name}</h4>

                <Image src={product.gallery[0]} alt=" product" />
                
          </ProductCardStyle>
        )}
          </ContainerProductList>
          </>
    );
    } else { this.fetchAll(); return "Loading..."; }
    }
};

export default AllCategories;

