import { PureComponent } from "react";
import { connect } from 'react-redux';
import { updateAttributeIndex } from "../store/cart";
import styled from "styled-components";
import { toBeEmpty } from "@testing-library/jest-dom/dist/matchers";

const FilterColor = styled.div`
width: 32px;
height: 32px;
cursor: pointer;
background-color: ${props => props.color};
${props => props.selected ? `outline-offset: 1px; outline: 1px solid #5ECE7B` : `none`};
`; 

class ShowAttributesInCart extends PureComponent {
    constructor(props) {
        super(props);
        this.state = {  
            // selectedAttributes: this.props.product.selectedAttributes,
            productIndex: this.props.productIndex,
           };
        this.colorSelect = this.colorSelect.bind(this);
        this.updateAttribute = this.updateAttribute.bind(this);
        this.setState({selectedAttributes: this.props.cartProducts[this.state.productIndex].selectedAttributes});
    }
     colorSelect(indexAttribute, index) {
         if (this.props.cartProduct[this.state.productIndex].selectedAttributes[indexAttribute][1] === index) {
            
            return true;
        } else { return false; }
    }
    updateAttribute(indexAttribute, index) {
        const temp = {productIndex: this.props.productIndex, indexAttribute, index}
        this.props.dispatch(updateAttributeIndex(temp));
    }

    render() {
        
        const { selectedAttributes, productIndex} = this.state;
            return (
            <>
                 {this.props.cartProducts[productIndex].attributes.map((obj, indexAttribute) => {
                     if (obj.type !== "swatch") {
                        
                        return (
                            <>
                                <p key={`${indexAttribute}_${obj.name} cart`} className="attribute-name">{obj.name}:</p>
                                <div key={`itemContainer_${indexAttribute}`} className="cart-item-container">
                                    {obj.items.map((item, index) => {
                                        return (
                                            <div key={`${index}_${item} cart`} className={ selectedAttributes[indexAttribute][1] === index ? "attribute-item-selected" : "attribute-item"}
                                                onClick={() => {this.updateAttribute(indexAttribute, index)  }}><p key={index}>{item.value}</p></div>
                                        );
                                    })}
                                </div>
                            </>
                        );
                    } else {
                        return (
                            <>
                                <p key={indexAttribute} className="attribute-name">{obj.name}:</p>
                                <div key={`colorContainer_${indexAttribute}`} className="attribute-color-container">
                                    {obj.items.map((item, index) => {
                                        return (
                                            <FilterColor key={index} color={item.value} selected={this.colorSelect(indexAttribute,index)} onClick={() => { this.setState({ selectedColor: index }) }} />
                                        );
                                    })}
                                </div>
                            </>
                        )
                    }
                })
                }
            </>
            )
        // return null;
        
    }
}
function mapStateToProps(state) {
    return {
        currency: state.currency.currency,
        cartProducts: state.cart.products,
        cartQuantity: state.cart.quantity,
    }
}
export default connect(mapStateToProps)(ShowAttributesInCart);
