import Header from "../header/Header";
import { Routes, Route} from "react-router-dom";
import { PureComponent } from "react";
import ProductList from "../../pages/ProductList";
// import TechPage from "../../pages/TechPage";
// import ClothesPage from "../../pages/ClothesPage";
import NotFoundPage from "../../pages/NotFoundPage";
import MiniCart from "../../pages/MiniCart";


class App extends PureComponent {
  getContext() {
    return {
      pathname: this.props.location.pathname
    }
  }

  render(){
    return (
    <div className="container">
       
      <Routes>
        <Route path="/" element={<Header />}>
          <Route index element={()=> <ProductList category={"all"} />} />
          <Route path="tech" element={()=> <ProductList category={"tech"}/>} />
          <Route path="clothes" element={()=> <ProductList category={"clothes"}  />} />
          <Route path="cart-overlay" element={<MiniCart />} />
          <Route path="*" element={<NotFoundPage />} />
        </Route>
      </Routes>
    </div>
  );}
}

export default App;
