 query Query($input: CategoryInput) {
  category(input: { title: "all" }) {
    name
    products {
      id
      name
      inStock
      gallery
      description
      category
      attributes {
        id
        name
        type
        items {
          displayValue
          value
          id
        }
      }
      prices {
        currency {
          symbol
          label
        }
        amount
      }
      brand
    }
  }
  currencies {
    label
    symbol
  }
}

`;
