import styled from "styled-components";
import { useState } from "react";
import { vector } from "../components/header/vector.png";


const Container = styled.div`
    width: 100%;
    height: 100vh;
    display: flex;
    overflow: hidden;
`;
const Arrow = styled.div`
width: 24px;
height: 24px;
background: rgba(0, 0, 0, 0.73);
display: flex;
align-items: center;
justify-content: center;
position: absolute;
top: 0;
bottom: 0;
left: ${props => props.direction === "left" && "10px"};
right: ${props => props.direction === "right" && "10px"};
margin: auto;
cursor: pointer;
z-index: 2;
`;
const Wrapper = styled.div`
height: 100%;
display: flex;
transition: all 1.5s ease;
transform: translateX(${props => props.slideIndex * -100}vw);
`;
const Slide = styled.div`
display: flex;
align-items: center;
width: 100vw;
height: 100vh;
`;
const ImageContainer = styled.div`
flex: 1;
height: 100%;
`;
const Image = styled.img`
/* height: 100%; */
margin: 0;
    width: 200px;
    height: 288px;
    object-fit: cover;
`


export default function Slider(props) {
    const [slideIndex, setSlideIndex] = useState(0);
    const handleClick = (direction) => {
        if (direction === "left") {
            setSlideIndex(slideIndex>0?slideIndex-1: 0)
        } else {
            setSlideIndex(slideIndex < 2 ? slideIndex + 1 : 0)
        }
    };
    return (
        <Container>
            <Arrow direction="left" onClick={()=>handleClick("left")}>K</Arrow>
            <Wrapper slideIndex={slideIndex} >
                <Slide>
                <ImageContainer>
                        <Image src={props.data[0]}></Image>
                    </ImageContainer>
                </Slide>
                <Slide>
                <ImageContainer>
                        <Image src={props.data[1]}></Image>
                    </ImageContainer>
                </Slide>
                <Slide>
                <ImageContainer>
                        <Image src={props.data[2]}></Image>
                    </ImageContainer>
                </Slide>
            </Wrapper>
            <Arrow direction="right" onClick={()=>handleClick("right")}>K</Arrow>
        </Container>
    )
}
