import { createSlice } from "@reduxjs/toolkit";

const cartSlice = createSlice({
    name: "cart",
    initialState: {
        products: [],
        quantity: 0,
        total: 0,
    },
    reducers: {
        addProductToCart: (state, action) => {
            state.quantity += action.payload.selectedProduct.quantity;
            state.products.push(action.payload.selectedProduct.product);
            state.total += action.payload.selectedProduct.price;
        },
        deleteProduct: (state, action) => {
            state.product = state.products.filter(product => product.id !== action.payload);
            state.quantity -= 1;
             state.total -= action.payload.price;
        }
    }
});

export const { addProductToCart, deleteProduct } = cartSlice.actions;
export default cartSlice.reducer;
