import { PureComponent} from "react";
import { gql, ApolloClient, InMemoryCache } from '@apollo/client';




class AllCategories extends PureComponent {
    constructor(props) {
        super(props);

        this.state = {
        data: [],
        loading: false,
        error: null,
        };
    }
    
 
    
    componentDidMount() {
        this.setState({ loading: true });
        const client = new ApolloClient({
  uri: 'http://localhost:4000/',
  cache: new InMemoryCache(),
        });
        const GET_ALL = gql`
        query Query {
  category(input: { title: "all" }) {
    name
    
    products {
      id
      
      name
      inStock
      gallery
      description
      category
      attributes {
        id
        
        name
        type
        items {
          displayValue
          
          value
          id
        }
      }
      prices {
        currency {
          symbol
          
          label
        }
        amount
        
      }
      brand
    }
  }
  currencies {
    label
    
    symbol
  }
} `;
        client.query({ query: GET_ALL }).then((result) => {
            console.log(result);
            this.setState({ data: result.data.category.products, loading: false });
            
        });

        }
        
    
render() {
   const { data, loading, error } = this.state;
    if (error) {
      return <p>Something went wrong</p>;
    }

    if (loading) {
      return <p>Loading ...</p>;
    }

    return (
      <ul>
        {data.map(product =>
            <li key={product.id}>
                <h4>{product.name}</h4>
            <a href={product.gallery[0]}>{product.description}</a>
          </li>
        )}
      </ul>
    );
    }
};

export default AllCategories;

