import { PureComponent } from "react";
import { Link } from "react-router-dom";
import { connect } from 'react-redux';
import { changeProductQuantity, decreaseProductQuantity, deleteProduct } from "../store/cart";
import plus from "../images/plus-square.png";
import minus from "../images/minus-square.png";
import "./MiniCart.css";

class MiniCart extends PureComponent {
    constructor(props) {
        super(props);
        this.state = {  };
    }
    render() {
        if (this.props.cartQuantity !== 0) {
            return (
                <div className="shop-cart">
                    {this.props.cartProducts.map((product, index) => {
                        return (
                    
                    <div className="mini-product-block">
                        <div className="block-1">
                            <div className="block-1-brand-name">Apollo <br /> Running Short</div>
                            <div className="minicart-price">$50</div>
                        </div>
                        <div className="block-2">
                            <div className="minicart-btn-wrapper">
                                <img className="minicart-btn-image" src={plus} alt="logo" />
                                <p className="minicart-counter" onClick={() => { }}>1</p>
                                <img className="minicart-btn-image" src={minus} alt="logo" />
                            </div>
                            <div className="minicart-image">hello</div>
                        </div>
                    </div>
                    
                    )
                    })}

                    
                    <div className="minicart-buttons">
                        <Link to="cart" className="link-view-bag"><p>view bag</p></Link>
                        <button className="btn-checkout">check out</button>
                    </div>
                </div>
        
            );
        }
    }
}
function mapStateToProps(state) {
    return {
        currency: state.currency.currency,
        cartProducts: state.cart.products,
        cartQuantity: state.cart.quantity,
    }
}
export default MiniCart;
