import { PureComponent} from "react";
import categoryQuery from "../other-components-and-functions/categoryQuery";
import ProductCard from "../components/productCard/ProductCard";

import "./ProductList.css";



class ProductList extends PureComponent {
   
    constructor(props) {
        super(props);
        
      this.state = {
        products: [],
        loading: false,
        error: null,
        category: this.props.category,
      };    
      
    }
    
    async componentDidMount() {
      this.setState({ loading: true });
      
      let productsRequest = await categoryQuery(this.state.category);
      this.setState({ products: productsRequest, loading: false });
        }
        
  componentDidUpdate(prevProps) {
  // Typical usage (don't forget to compare props):
  if (this.props.category !== prevProps.category) {
    this.setState({ product: [], category: null });
  }
}
  
render() {
  const { products, loading, error, category } = this.state;
  
    if (error) {
      return <p>Something went wrong</p>;
    }

    if (loading) {
      return <p>Loading ...</p>;
    }
    if(products !== null)
    {
      return (
      <>
      <div className="product-list-header"></div>
      <div className="product-list-container">
            {products.map(product =>
              <ProductCard product={product} key={product.id} />
        )}
          </div>
          </>
    );
    } else {      
      return "Loading...";
    }
    }
};


export default ProductList;
