import { PureComponent } from "react";
import { useParams } from "react-router-dom";
import { Interweave } from 'interweave';
import getProductById from "../other-components-and-functions/getProductById";
import styled from "styled-components";
import { connect } from 'react-redux';
import "./ProductPage.css";


const FilterColor = styled.div`
width: 32px;
height: 32px;
cursor: pointer;
background-color: ${props => props.color};
border: ${props => props.selected ? `1px solid #5ECE7B` : `none`};
`;

function withParams(Component) {
  return props => <Component {...props} params={useParams()} />;
}

class ProductPage extends PureComponent {
    constructor(props) {
        super(props);
        this.state = {
            product: [],
            loading: false,
            error: null,
            currency: this.props.currency,
            prices: [],
            gallery: [],
            attributes: [],
            amount: null,
            toggleImage: 0,
            itemSelected: 0,
            colorFlag: false,
        };
        this.getPrice = this.getPrice.bind(this);
    } 
    getPrice() { 
        this.state.prices.map((item) => {
            if (item.currency.symbol === this.props.currency) {
                return this.setState({ amount: item.amount});
            } else { return null; }
        })
    }
    async fetchData(props) {
        let productRequest = await getProductById(this.props);
        this.setState({ prices: productRequest.prices });
        this.getPrice();
        const gallery = [];
        const attributes = [];
        Object.assign(gallery, productRequest.gallery);
        Object.assign(attributes, productRequest.attributes);

        return  this.setState({ product: productRequest, gallery: gallery, attributes: attributes, loading: false });
    }
    componentDidMount() {
        let { id } = this.props.params;
        console.log(id);
        this.setState({ loading: true });
        this.fetchData(id);
    }
    componentDidUpdate(props, state) {
     if (this.state.currency !== this.props.currency) {
        this.setState({ currency: this.props.currency });
        this.getPrice();
    }
  }
    
    render() {
        const { product, loading, error, amount, gallery,toggleImage, attributes } = this.state;
        console.log(product);
        console.log(attributes);
        if (error) {
            return <p>Something went wrong</p>;
        }

        if (loading) {
            return <p>Loading ...</p>;
        }
        if (product !== null && amount !== null) {
            return (
                
                <div className="product-container">
                    <div className="small-image-wrapper">
                        {gallery.map((img, index) => {
                                return <img className="small-image" src={img} key={index} alt="prod" onClick={() => {this.setState({toggleImage: index})}} />                            
                        })}
                    </div>
                    <img className="big-image" src={gallery[toggleImage]} alt=""/>
                    <div className="content-block">
                        <p className="brand">{product.brand}</p>
                        <p className="name">{product.name} </p>
                        {attributes.map((obj, index) => {
                            if (obj.type !== "swatch") {
                                return (
                                    <>
                                        <p key={index} className="attribute-name">{obj.name}</p>
                                        <div className="attribute-item-container">
                                            {obj.items.map((item, index) => {                                               
                                                return (
                                                    <div  key={index} className={this.state.itemSelected === index ? "attribute-item-selected" : "attribute-item"} onClick={()=>{this.setState({itemSelected: index})}}><p>{item.value}</p></div>
                                                );
                                            })}
                                        </div>
                                    </>
                                );
                            } else {
                                return (
                                <>
                                        <p key={index} className="attribute-name">{obj.name}</p>
                                        <div className="attribute-color-container">
                                            {obj.items.map((item, index) => {   
                                                let tempColor = false;
                                                if (index === 0) { tempColor = true; this.setState({colorFlag: tempColor})};
                                                return (
                                                    <FilterColor key={index} color={ item.value} selected={this.state.colorFlag} onClick={()=>{this.setState({colorFlag: true})}} />
                                                );
                                            })}
                                        </div>
                                    </>
                                )
                            }
                        })}
                        
                       
                        <p className="attribute-name">Price:</p>
                        <p className="price">{this.props.currency} {amount }</p>
                        <button className="button-style">Add to cart</button>
                        <p className="description"><Interweave content={product.description} /></p>
                    </div>
                </div>
            );
        } else {
            return "Loading...";
        }
    }
}
function mapStateToProps(state) {
  return {
    currency: state.currency.currency
  }
};
export default connect(mapStateToProps)(withParams(ProductPage));
