import { PureComponent } from "react";
import { useQuery, gql } from '@apollo/client';

function AllCategories() {

const categoriesFullInfo = gql`
 query Query($input: CategoryInput) {
  category(input: { title: "all" }) {
    name
    __typename @skip(if: true)
    products {
      id
      __typename @skip(if: true)
      name
      inStock
      gallery
      description
      category
      attributes {
        id
        __typename @skip(if: true)
        name
        type
        items {
          displayValue
          __typename @skip(if: true)
          value
          id
        }
      }
      prices {
        currency {
          symbol
          __typename @skip(if: true)
          label
        }
        amount
        __typename @skip(if: true)
      }
      brand
    }
  }
  currencies {
    label
    symbol
    __typename @skip(if: true)
  }
}

`;
function DisplayAllCategories() {
    const { loading, error, data } = useQuery( categoriesFullInfo );

  if (loading) return <p>Loading...</p>;
    if (error) return <p>Unfortunately, something is wrong with database. Please try later...</p>;
    console.log(data.category);
    return "Hello all categories";

}}
