import styled from "styled-components";
import { useState } from "react";
import vector from "../images/vector-white.png";
const Container = styled.div`
    margin: 24px 0px 24px 24px;
    width: 200px;
    height: 288px;
    /* object-fit: cover; */
    display: flex;
    overflow: hidden;
    position: relative;
`;
const Arrow = styled.div`
    width: 24px;
    height: 24px;
    background: rgba(0, 0, 0, 0.73);
    display: flex;
    align-items: center;
    justify-content: center;
    position: absolute;
    top: 248px;
    
    left: ${props => props.direction === "left" && "128px"};
    right: ${props => props.direction === "right" && "16px"};
    margin: auto;
    cursor: pointer;
    z-index: 2;
`;
const ArrowImage = styled.img`
transform: ${props => props.direction === "right" && "rotate(-180deg)"};
/* transform: matrix(1, 0, 0, -1, 0, 0); */
`;
const Wrapper = styled.div`
 /* height: 100%; */
 display: flex;
 transition: all 1s ease;
 transform: translateX(${props => props.slideIndex * -200}px);
 `;


const Image = styled.img`
     margin: 0;
     width: 200px;
     height: 288px;
     object-fit: cover;
 `;

export default function Slider(props) {
    const [slideIndex, setSlideIndex] = useState(0);
    const handleClick = (direction) => {
        if (direction === "left") {
            setSlideIndex(slideIndex>0?slideIndex-1: 0)
        } else {
            setSlideIndex(slideIndex < 2 ? slideIndex + 1 : 0)
        }
    };
    return (
        <>
            <Arrow direction="left" onClick={() => handleClick("left")}> <ArrowImage direction="left" src={ vector} alt='logo'></ArrowImage> </Arrow>
            <Wrapper slideIndex={slideIndex} >
                
               
                        <Image src={props.data[0]} alt="logo"></Image>
                    
                
                
                
                        <Image src={props.data[1]}></Image>
                    
                
                
                
                        <Image src={props.data[2]}></Image>
                   
                
            </Wrapper>
            <Arrow direction="right" onClick={()=>handleClick("right")}><ArrowImage direction="right" src={ vector} alt='logo'></ArrowImage></Arrow>
        </>
    )
}
