import { PureComponent } from "react";
import styled from "styled-components";

const FilterColor = styled.div`
width: 32px;
height: 32px;
cursor: pointer;
background-color: ${props => props.color};
${props => props.selected ? `outline-offset: 1px; outline: 1px solid #5ECE7B` : `none`};
`; 

class ShowAttributesInCart extends PureComponent {
    constructor(props) {
        super(props);
        this.state = { 
            product: this.props.product,
            colorFlag: false,
            selectedColor: 0,
            selectedAttributes: this.props.attributesInStore,
            
        };
        this.colorSelect = this.colorSelect.bind(this);
    }
     colorSelect(index) {
        if (this.state.selectedColor === index) {
            return true;
        } else { return false; }
    }
    render() {
        const { selectedAttributes, product } = this.state;
        if (selectedAttributes[0][0] === "default") {
            return (<>
                {product.attributes.map((obj, indexAttribute) => {
                    let attributeNameIndex = obj.name;
                    this.setState({ [attributeNameIndex]: this.state[attributeNameIndex] });
                    if (this.state[attributeNameIndex] === undefined) { this.setState({ [attributeNameIndex]: 0 }) }
                    if (obj.type !== "swatch") {
                        return (
                            <>
                                <p key={`${indexAttribute}_${obj.name} cart`} className="attribute-name">{obj.name}:</p>
                                <div key={`itemContainer_${indexAttribute}`} className="cart-item-container">
                                    {obj.items.map((item, index) => {
                                        return (
                                            <div key={`${index}_${item} cart`} className={this.state[attributeNameIndex] === index ? "attribute-item-selected" : "attribute-item"}
                                                onClick={() => { this.setState({ [attributeNameIndex]: index }) }}><p key={index}>{item.value}</p></div>
                                        );
                                    })}
                                </div>
                            </>
                        );
                    } else {
                        return (
                            <>
                                <p key={indexAttribute} className="attribute-name">{obj.name}:</p>
                                <div key={`colorContainer_${indexAttribute}`} className="attribute-color-container">
                                    {obj.items.map((item, index) => {
                                        return (
                                            <FilterColor key={index} color={item.value} selected={this.colorSelect(index)} onClick={() => { this.setState({ selectedColor: index }) }} />
                                        );
                                    })}
                                </div>
                            </>
                        )
                    }
                })
                }
            </>
            )
        } else {
            return (
                <>
                
                   
                </>
            );
        }
    }
}

export default ShowAttributesInCart;
