import { gql } from '@apollo/client';

export const GET_CATEGORY = gql`
        query Query($id: String!) {
  category(input: { title: $id}) {
    name 
    products {
      id
      name
      inStock
      gallery
      description
      category
      attributes {
        id
        name
        type
        items {
          displayValue
          value
          id
        }
      }
      prices {
        currency {
          symbol
          label
        }
        amount
      }
      brand
    }
  }
  
} `;
