import { PureComponent } from "react";
import logo from "./logo-shop.png";
import cartIcon from "./bag-empty.png";
import {  Outlet } from "react-router-dom";
import Categories from "../Categories";
import "./Header.css";



class Header extends PureComponent {
    constructor(props) {
        super(props);
        this.state = {
            visiblePopup: false,
        }
    }
    // componentDidMount() {
        
    // const  toggleVisiblePopup = () => {
    //     this.setVisiblePopup(!visiblePopup);
    //     }
    // }
    render() {
        // const { visiblePopup } = this.state;
        return (
            <> 
                <div className="navbar-container">
                <div className="navbar-row">
                        <div className="navbar-menu"> 
                            <Categories  />
                       
                    </div>
                    
                       
                   <div> 
                        <img className="navbar-icon" src={logo} alt="logo" /> 
                    </div>
                        <div className="navbar-right">
                            <div className="selected-currency">
                            <div className="currency-info" >
                                $ ^
                            </div>
                            <ul className="list-currency">
                                <li>1</li>
                                <li>2</li>
                                <li>3</li>
                                <li>4</li>
                                    </ul></div>
                                
                            <button ><img className="cart-icon" src={cartIcon} alt="cart-logo" /> </button>
                        </div>
                </div>
                </div>
                <main className="container-main">
                <Outlet />
                </main>
            </>)
    }
};
export default Header;
