import { PureComponent } from "react";
import { Outlet } from "react-router-dom";
import { connect } from 'react-redux';
import Categories from "../Categories";
import CurrencyList from "../../other-components-and-functions/CurrencyList";
import logo from "./logo-shop.png";
import cartIcon from "./bag-empty.png";
import currencyBtn from "./vector.png";
import styled from 'styled-components';
import "./Header.css";

const ModalShadow = styled.div`
 position: fixed;
  top:80px;
  left:0;
  height: 100%;
  width: 100%;
  background: rgba(57, 55, 72, 0.22);
  z-index: 1;
`;

class Header extends PureComponent {
    constructor(props) {
        super(props);
        this.state = {
            currencyOpen: false,
            cartOpen: false,
            currency: null,
        }
    }
    
    render() {   
        return (
            <>
                {this.state.cartOpen && (
                        <ModalShadow onClick={() => this.setState({ cartOpen: !this.state.cartOpen })} />)}
                <div className="navbar-container">
                    
                <div className="navbar-row">
                    <div className="navbar-menu"> 
                        <Categories  />
                    </div>  
                    <div> 
                        <img className="navbar-icon" src={logo} alt="logo" /> 
                    </div>
                    <div className="navbar-right">    
                        <div className={`currency-icon ${this.state.currencyOpen && "activeCurrency"}`} onClick={() => this.setState({currencyOpen: !this.state.currencyOpen})}> $<img src={currencyBtn} alt="currency"  /></div>
                            {this.state.currencyOpen && (<div className="shop-cart"><CurrencyList /></div>)}   
                             
                            <img className={`cart-icon ${this.state.cartOpen && "activeCart"}`} src={cartIcon} alt="cart-logo" onClick={() => this.setState({ cartOpen: !this.state.cartOpen })}  /> 
                            {this.state.cartOpen && ( <div className="shop-cart">Hello</div>)}
                        </div>
                    </div>
                </div>
                <main className="container-main">
                <Outlet />
                </main>
                
                </> 
                );
    }
};
export default Header;
