import { PureComponent} from "react";
import styled from "styled-components";
import categoryQuery from "../other-components-and-functions/categoryQuery";
import ProductCard from "../components/productCard/ProductCard";

const Header = styled.div`
  font-size: 42px;
  font-weight: 400;
  line-height: 160%;
  color: #1D1F22;
  margin-left: 100px;
  text-align: left;
`;
const ContainerProductList = styled.div`
  display: flex;
  flex-wrap: wrap;
  flex-direction: row;
  margin: 100px 100px 190px 100px;
  align-items: center;
  row-gap: 100px;
  column-gap: 40px;

`;




class ClothesPage extends PureComponent {
    constructor(props) {
        super(props);
        
        this.state = {
        products: [],
        loading: false,
        error: null,
        };
    }
    
    
 
    
   
        
    
render() {
   const { data, loading, error } = this.state;
    if (error) {
      return <p>Something went wrong</p>;
    }

    if (loading) {
      return <p>Loading ...</p>;
    }

    return (
      <ul>
        {data.map(product =>
            <li key={product.id}>
                <h4>{product.name}</h4>
                
                <img src={product.gallery[0]} alt=" product" />
                
          </li>
        )}
      </ul>
    );
    }
};

export default ClothesPage;
