import { PureComponent } from "react";
import styled from "styled-components";

import vector from "../images/vector-white.png";
const Container = styled.div`
    margin: 24px 0px 24px 24px;
    width: 200px;
    height: 288px;
    /* object-fit: cover; */
    display: flex;
    overflow: hidden;
    position: relative;
`;
const Arrow = styled.div`
    width: 24px;
    height: 24px;
    background: rgba(0, 0, 0, 0.73);
    display: flex;
    align-items: center;
    justify-content: center;
    position: absolute;
    top: 248px;
    
    left: ${props => props.direction === "left" && "128px"};
    right: ${props => props.direction === "right" && "16px"};
    margin: auto;
    cursor: pointer;
    z-index: 2;
`;
const ArrowImage = styled.img`
transform: ${props => props.direction === "right" && "rotate(-180deg)"};
/* transform: matrix(1, 0, 0, -1, 0, 0); */
`;
const Wrapper = styled.div`
 /* height: 100%; */
 display: flex;
 transition: all 1s ease;
 transform: translateX(${props => props.slideIndex * -200}px);
 `;


const Image = styled.img`
     margin: 0;
     width: 200px;
     height: 288px;
     object-fit: cover;
 `;
class Slider extends PureComponent {
    constructor(props) {
        super(props);
        this.state = { 
            slideIndex: 0,
            gallery: this.props.data,
        };
        this.handleClick = this.handleClick.bind(this);
    }
    handleClick = (direction) => {
        if (direction === "left") {
            let temp = this.state.slideIndex - 1;
            this.state.slideIndex > 0 ? this.setState({slideIndex: temp}) : this.setState({slideIndex: 0})
        } else {
            let temp = this.state.slideIndex + 1;
            this.state.slideIndex < this.state.gallery.length ? this.setState({slideIndex: temp}) : this.setState({slideIndex: 0})
        }
    };
    render() {
        const { gallery, slideIndex } = this.state;
        return (
          <>
             <Arrow direction="left" onClick={() => this.handleClick("left")}> <ArrowImage direction="left" src={ vector} alt='logo'></ArrowImage> </Arrow>
             <Wrapper slideIndex={slideIndex} >               
                         <Image src={gallery[0]} alt="logo"></Image>
                         <Image src={gallery[1]}></Image>
                         <Image src={gallery[2]}></Image>
             </Wrapper>
             <Arrow direction="right" onClick={()=>this.handleClick("right")}><ArrowImage direction="right" src={ vector} alt='logo'></ArrowImage></Arrow>
         </>
        );
    }
}

export default Slider;
