import { PureComponent} from "react";
import { gql, ApolloClient, InMemoryCache } from '@apollo/client';




class AllCategories extends PureComponent {
    constructor(props) {
        super(props);
        
        this.state = {
        data: [],
        loading: false,
        error: null,
        };
    }
    fetchAll = () => {
        const client = new ApolloClient({
  uri: 'http://localhost:4000/',
  cache: new InMemoryCache(),
        });
        const GET_ALL = gql`
        query Query {
  category(input: { title: "clothes" }) {
    name
    
    products {
      id
      
      name
      inStock
      gallery
      description
      category
      attributes {
        id
        
        name
        type
        items {
          displayValue
          
          value
          id
        }
      }
      prices {
        currency {
          symbol
          
          label
        }
        amount
        
      }
      brand
    }
  }
  currencies {
    label
    
    symbol
  }
} `;
        client.query({ query: GET_ALL }).then((result) => {
            console.log(result);
            this.setState({ data: result.data.category.products, loading: false });
            },
            (error) => {
            this.setState({loading:true,error:error})
        });
    }
 
    
    componentDidMount() {
        this.setState({ loading: true });
        this.fetchAll();
        }
        
    
render() {
   const { data, loading, error } = this.state;
    if (error) {
      return <p>Something went wrong</p>;
    }

    if (loading) {
      return <p>Loading ...</p>;
    }
    if(data !== [])
    {return (
      <ul>
        {data.map(product =>
            <li key={product.id}>
                <h4>{product.name}</h4>

                <img src={product.gallery[0]} alt=" product" />
                
          </li>
        )}
      </ul>
    );
    } else { this.fetchAll(); return "Loading..."; }
    }
};

export default AllCategories;

