import { PureComponent } from "react";
import { connect } from 'react-redux';
import plus from "../images/plus-square.png";
import minus from "../images/minus-square.png";

import "./Cart.css";
import "./ProductPage.css";

class Cart extends PureComponent {
    constructor(props) {
        super(props);
        this.state = {
            products: this.props.cartProducts,
            currency: this.props.currency,
            cartQuantity: this.props.cartQuantity,
            selectedAttributesArray: this.props.selectedAttributesArray,
        };
    }
    
    render() {
        return (
            <>
                <div className="cart-header">CART</div>
                <div className="cart-product-block">
                    <div className="part-1">
                        <div className="cart-brand">Apollo</div>
                        <div className="cart-name">Shorts</div>
                        <div className="cart-price">$50</div>
                        <p className="attribute-name">Size:</p>
                        <div className="cart-item-container">
                            <div className="attribute-item-selected"><p>S</p></div>
                            <div className="attribute-item"><p>M</p></div>
                        </div>
                    </div>
                    <div className="part-2">
                        <div className="btn-wrapper">
                            <img className="btn-image" src={plus} alt="logo" />
                            <p className="counter" onClick={()=>{}}>1</p>
                            <img className="btn-image" src={minus} alt="logo" /> 
                        </div>
                        <div className="slider-container" >Hello</div>
                    </div>
                </div>
                <div className="order-description">
                    <div className="order-column-1">
                        <p className="order-text">Tax 21%:</p>
                        <p className="order-text">Quantity:</p>
                        <p className="order-text">Total:</p>
                    </div>
                    <div className="order-column-2">
                     <p className="order-quantity">$40</p>
                     <p className="order-quantity">3</p>
                     <p className="order-quantity">$240</p>
                    </div>
                </div>
                <button type="submit" className="order-btn"><p> order</p></button>
            </>
        )
    }
}
function mapStateToProps(state) {
    return {
        currency: state.currency.currency,
        cartProducts: state.cart.products,
        cartQuantity: state.cart.quantity,
        selectedAttributesArray: state.cart.selectedAttributesArray,
        
    }
}
export default connect(mapStateToProps)(Cart);
