import { PureComponent } from "react";
import { addProductToCart } from "../store/cart";
import styled from "styled-components";
import "../pages/ProductPage.css";

const FilterColor = styled.div`
width: 32px;
height: 32px;
cursor: pointer;
background-color: ${props => props.color};
${props => props.selected ? `outline-offset: 1px; outline: 1px solid #5ECE7B` : `none`};
`;

class Attributes extends PureComponent {
    constructor(props) {
        super(props);
        this.state = {
            attributes: this.props.attributes,
            colorFlag: false,
            selectedColor: 0,
        }
        this.colorSelect = this.colorSelect.bind(this);
        this.addToCart = this.addToCart.bind(this);
        this.returnAttributes = this.returnAttributes.bind(this);
    }

    colorSelect(index) {
        if (this.state.selectedColor === index) {
            return true;
        } else { return false; }
    }

    returnAttributes() {
        this.state.attributes.map((obj, indexAttribute) => {
        let attributeNameIndex = obj.name;
        this.setState({ [attributeNameIndex]: this.state[attributeNameIndex] });
        if (this.state[attributeNameIndex] === undefined) { this.setState({ [attributeNameIndex]: 0 }) }
        if (obj.type !== "swatch") {
            return (
                <>
                    <p key={indexAttribute} className="attribute-name">{obj.name}:</p>
                    <div className="attribute-item-container">
                        {obj.items.map((item, index) => {
                                                
                            return (
                                <div key={index} className={this.state[attributeNameIndex] === index ? "attribute-item-selected" : "attribute-item"}
                                    onClick={() => { this.setState({ [attributeNameIndex]: index }) }}><p>{item.value}</p></div>
                            );
                        })}
                    </div>
                </>
            );
            } else {
                return (
                    <>
                        <p key={indexAttribute} className="attribute-name">{obj.name}:</p>
                        <div className="attribute-color-container">
                            {obj.items.map((item, index) => {
                                return (
                                    <FilterColor key={index} color={item.value} selected={this.colorSelect(index)} onClick={() => { this.setState({ selectedColor: index }) }} />
                                );
                            })}
                        </div>
                    </>
                )   }
        })
}
    render() {
        const { attributes } = this.state;
        console.log(attributes);
        
        return this.returnAttributes();
    }
}
export default Attributes;
