import { gql, ApolloClient, InMemoryCache } from '@apollo/client';

export default function categoryQuery(props) {
    const client = new ApolloClient({
  uri: 'http://localhost:4000/',
  cache: new InMemoryCache(),
        });
        const GET_CATEGORY = gql`
        query Query {
  category(input: { title: "${props}" }) {
    name
    
    products {
      id
      
      name
      inStock
      gallery
      description
      category
      attributes {
        id
        
        name
        type
        items {
          displayValue
          
          value
          id
        }
      }
      prices {
        currency {
          symbol
          
          label
        }
        amount
        
      }
      brand
    }
  }
  currencies {
    label
    
    symbol
  }
} `;
    // const response = {
    //     data: [],
    //     loading: false,
    //     error: null,
    // }
    
    client.query({ query: GET_CATEGORY }).then((result) => {
            console.log(result);
        // response.data = result.data.category.products;
        // response.loading = false;
        const response = [...result.data.category.products];
        return response;
    },
        (error) => {
            console.log(error);
            return <div>Something went wrong</div>
        });
    
    
}
