import { PureComponent } from "react";
import { useParams } from "react-router-dom";
import { Interweave } from 'interweave';
import getProductById from "../other-components-and-functions/getProductById";
import styled from "styled-components";
import { connect } from 'react-redux';
import "./ProductPage.css";
import { addProductToCart } from "../store/cart";


const FilterColor = styled.div`
width: 32px;
height: 32px;
cursor: pointer;
background-color: ${props => props.color};
${props => props.selected ? `outline-offset: 1px; outline: 1px solid #5ECE7B` : `none`};
`;

function withParams(Component) {
  return props => <Component {...props} params={useParams()} />;
}

class ProductPage extends PureComponent {
    constructor(props) {
        super(props);
        this.state = {
            product: [],
            loading: false,
            error: null,
            currency: this.props.currency,
            prices: [],
            gallery: [],
            attributes: [],
            amount: null,
            toggleImage: 0,
            itemSelected: 0,
            properties: {},
            colorFlag: false,
            selectedColor: 0, 
        };
        this.getPrice = this.getPrice.bind(this);
        this.controlSelect = this.controlSelect.bind(this);
        this.addToCart = this.addToCart.bind(this);
    } 
    getPrice() { 
        this.state.prices.map((item) => {
            if (item.currency.symbol === this.props.currency) {
                return this.setState({ amount: item.amount});
            } else { return null; }
        })
    }
    async fetchData(props) {
        let productRequest = await getProductById(this.props);
        this.setState({ prices: productRequest.prices });
        this.getPrice();
       
        // const gallery = Object.values(productRequest.gallery);
        // const attributes = Object.values(productRequest.attributes);
        // const gallery = [];
        // const attributes = [];
        // Object.assign(gallery, productRequest.gallery);
        // Object.assign(attributes, productRequest.attributes);

        return  this.setState({ product: productRequest, gallery: productRequest.gallery, attributes: productRequest.attributes, loading: false });
    }
    componentDidMount() {
        let { id } = this.props.params;
        console.log(id);
        this.setState({ loading: true });
        this.fetchData(id);
    }
    componentDidUpdate(props, state) {
     if (this.state.currency !== this.props.currency) {
        this.setState({ currency: this.props.currency });
        this.getPrice();
    }
  }
    controlSelect(index) {
        if (this.state.selectedColor === index) {
            return true;
        } else { return false; }
    }
    addToCart(product) {
        const selectedProduct={product: product, quantity: 1, price: this.state.amount }
        this.props.dispatch(addProductToCart({ selectedProduct }));
    }
    render() {
        const { product, loading, error, amount, gallery,toggleImage, attributes } = this.state;
        console.log(product);
        console.log(attributes);

        if (error) {
            return <p>Something went wrong</p>;
        }

        if (loading) {
            return <p>Loading ...</p>;
        }
        if (product !== null && amount !== null) {
            return (
                
                <div className="product-container">
                    <div className="small-image-wrapper">
                        {gallery.map((img, index) => {
                                return <img className="small-image" src={img} key={index} alt="prod" onClick={() => {this.setState({toggleImage: index})}} />                            
                        })}
                    </div>
                    <img className="big-image" src={gallery[toggleImage]} alt=""/>
                    <div className="content-block">
                        <p className="brand">{product.brand}</p>
                        <p className="name">{product.name} </p>
                        {attributes.map((obj, indexAttribute) => {
                            let attributeName = obj.name;
                            // this.setState({ properties: { [attributeName]: 0 } });
                            
                            if (obj.type !== "swatch") {
                                return (
                                    <>
                                        <p key={indexAttribute} className="attribute-name">{obj.name}:</p>
                                        <div className="attribute-item-container">
                                            {obj.items.map((item, index) => {
                                                console.log(item, this.state);
                                                return (
                                                    <div key={index} className={this.state.itemSelected === index ? "attribute-item-selected" : "attribute-item"} 
                                                        onClick={() => { this.setState({ itemSelected: index, properties: { [attributeName]: index } }) }}><p>{item.value}</p></div>
                                                    // <div key={index} className={this.state.itemSelected === index ? "attribute-item-selected" : "attribute-item"}
                                                    //     onClick={() => {  this.setState({ itemSelected: index }) }}>
                                                    //     <p>{item.value}</p></div>
                                                );
                                            })}
                                        </div>
                                    </>
                                );
                            } else {
                                return (
                                <>
                                        <p key={indexAttribute} className="attribute-name">{obj.name}:</p>
                                        <div className="attribute-color-container">
                                            {obj.items.map((item, index) => {   
                                            
                                                return (
                                                    <FilterColor key={index} color={ item.value} selected={this.controlSelect(index)} onClick={()=>{this.setState({selectedColor: index})}} />
                                                );
                                            })}
                                        </div>
                                    </>
                                )
                            }
                        })}                        
                        <p className="attribute-name">Price:</p>
                        <p className="price">{this.props.currency} {amount }</p>
                        <button className="button-style" onClick={()=> this.addToCart(product) }>Add to cart</button>
                        <p className="description"><Interweave content={product.description} /></p>
                    </div>
                </div>
            );
        } else {
            return "Loading...";
        }
    }
}
function mapStateToProps(state) {
  return {
      currency: state.currency.currency, 
  }
};
export default connect(mapStateToProps)(withParams(ProductPage));
