import { PureComponent } from "react";
import { useQuery, gql } from '@apollo/client';
import { Query } from "react-apollo";
const GET_ALL = gql`
        query Query($input: CategoryInput) {
  category(input: { title: "all" }) {
    name
    products {
      id
      name
      inStock
      gallery
      description
      category
      attributes {
        id
        name
        type
        items {
          displayValue
          value
          id
        }
      }
      prices {
        currency {
          symbol
          label
        }
        amount
      }
      brand
    }
  }
  currencies {
    label
    symbol
  }
}

        `;
const CategoriesQuery = () => {
    return (
        <Query query={GET_ALL}>
            {({ loading, error, data }) => {
                if (loading) return <p>Loading...</p>;
                if (error) return <p>Unfortunately, something is wrong with database. Please try later...</p>;
    return <AllCategories data={data.category} />   
            }}
        </Query>
    )
}
   

class AllCategories extends PureComponent {
    constructor(props) {
        super(props);
        this.state = {
        data: [],
        loading: false,
        error: null,
        };
    }
 
    
    componentDidMount() {
        this.setState({ loading: true });
            
 
        }
        
    
render() {
    const { data, loading, error } = this.state;
    if (loading) return <p>Loading...</p>;
    if (error) return <p>Unfortunately, something is wrong with database. Please try later...</p>;
    return console.log(data);
    }
};

export default AllCategories;
