import { PureComponent} from "react";





class ClothesPage extends PureComponent {
    constructor(props) {
        super(props);
        
        this.state = {
        data: [],
        loading: false,
        error: null,
        };
    }
    
    
 
    
    componentDidMount() {
        this.setState({ loading: true });
       
        }
        
    
render() {
   const { data, loading, error } = this.state;
    if (error) {
      return <p>Something went wrong</p>;
    }

    if (loading) {
      return <p>Loading ...</p>;
    }

    return (
      <ul>
        {data.map(product =>
            <li key={product.id}>
                <h4>{product.name}</h4>
                
                <img src={product.gallery[0]} alt=" product" />
                
          </li>
        )}
      </ul>
    );
    }
};

export default ClothesPage;
