import { PureComponent } from "react";
import styled from "styled-components";

class ShowAttributesInCart extends PureComponent {
    constructor(props) {
        super(props);
        this.state = { 
            attributesProduct: this.props.product.attributes,
            selectedAttributes: this.props.attributesInStor,
            
         };
    }
    render() {
        const { selectedAttributes, attributesProduct } = this.state;
        if (selectedAttributes[0][0] === "default") {
            return (<>
                {attributesProduct.map((obj, indexAttribute) => {
                    let attributeNameIndex = obj.name;
                    this.setState({  [attributeNameIndex]: this.state[attributeNameIndex]});
                    if(this.state[attributeNameIndex] === undefined) {this.setState({ [attributeNameIndex]: 0})}
                    if (obj.type !== "swatch") {
                        return (
                            <>
                                <p key={indexAttribute} className="attribute-name">{obj.name}:</p>
                                        <div key={`itemContainer_${indexAttribute}`} className="attribute-item-container">
                                            {obj.items.map((item, index) => {
                                                
                                                return (
                                                    <div key={index} className={this.state[attributeNameIndex] === index ? "attribute-item-selected" : "attribute-item"} 
                                                        onClick={() => { this.setState({  [attributeNameIndex]: index }) }}><p key={index}>{item.value}</p></div>
                                                );
                                            })}
                                        </div>
                                    </>
                                );
                            } else {
                                return (
                                <>
                                        <p key={indexAttribute} className="attribute-name">{obj.name}:</p>
                                        <div key={`colorContainer_${indexAttribute}`}  className="attribute-color-container">
                                            {obj.items.map((item, index) => {   
                                                return (
                                                    <FilterColor key={index} color={ item.value} selected={this.colorSelect(index)} onClick={()=>{this.setState({selectedColor: index})}} />
                                                );
                                            })}
                                        </div>
                                    </>
                                )
                            }
                })
                }
                </>
            )
        }
        return (
            <>
                
{/*             
             <p className="attribute-name"key={index}>{selectedAttributesArray[0][0] }:</p>
                            <div className="cart-item-container" key={`${index} product container`}>
                                 <div className="attribute-item-selected"><p>S</p></div>
                                <div className="attribute-item"><p>M</p></div>
            </div> */}
            </>
        );
    }
}

export default ShowAttributesInCart;
