import { PureComponent} from "react";
import getProductsByCategory from "../other-components-and-functions/getProductsByCategory";
import ProductCard from "../components/productCard/ProductCard";

import "./ProductList.css";



class ProductList extends PureComponent {
   
    constructor(props) {
        super(props);
        
      this.state = {
        products: [],
        loading: false,
        error: null,
        category: 'all',
        orders:[],
      };    
      this.addToOrder = this.addToOrder.bind(this);
  }
  
    static getDerivedStateFromProps(props, state) {
    if (props.category !== state.prevCategory) {
      return { products: null, prevCategory: props.category   };
    }
    return null;
  }
   
  async fetchData(props) {
    let productsRequest = await getProductsByCategory(this.state.category);
    return  this.setState({ products: productsRequest, loading: false });
}
  componentDidMount() {
    this.setState({ loading: true });
    this.fetchData(this.props.category);
  }
    // async componentDidMount() {
    //   this.setState({ loading: true });
      
    //   let productsRequest = await categoryQuery(this.state.category);
    //   this.setState({ products: productsRequest, loading: false });
    //     }
        
   componentDidUpdate(prevProps, prevState) {
    if (this.state.products === null) {
      this.fetchData(this.props.category);
    }
  }
  
render() {
  const { products, loading, error} = this.state;
  
    if (error) {
      return <p>Something went wrong</p>;
    }

    if (loading) {
      return <p>Loading ...</p>;
    }
    if(products !== null )
    {
      return (
      <>
      <div className="product-list-header"></div>
      <div className="product-list-container">
            {products.map(product =>
              <ProductCard product={product} key={product.id} />
        )}
          </div>
          </>
    );
    } else {      
      return "Loading...";
    }
  }
  addToOrder(item) {
    this.setState({orders:[...this.state.orders,item]})
  }
};


export default ProductList;
