import { PureComponent} from "react";
import { gql, ApolloClient, InMemoryCache } from '@apollo/client';
import styled from "styled-components";


const Header = styled.div`
  font-size: 42px;
  font-weight: 400;
  line-height: 160%;
  color: #1D1F22;
  margin-left: 100px;
  text-align: left;
`;
const ContainerProductList = styled.div`
  display: flex;
  flex-wrap: wrap;
  flex-direction: row;
  margin: 100px 100px 190px 100px;
  align-items: center;
  row-gap: 100px;
  column-gap: 40px;

`;
const ProductCardStyle = styled.div`

flex: 1;
order: 0;
flex-grow: 0;
background: #FFFFFF;
padding: 16px;
width: 380px;
/* min-width: 380px; */
/* height: 444px; */

`;

const Image = styled.img`
width: 354px;
height: 330px;
object-fit: cover;
`;

const Title = styled.p`
font-weight: 300;
font-size: 18px;
line-height: 160%;
text-align: left;
color: #1D1F22;
margin: 24px 0 0 0;
`;
const Price = styled.p`
margin: 0;
font-weight: 500;
font-size: 18px;
line-height: 160%;
text-align: left;
color: #1D1F22;
`;

class AllCategories extends PureComponent {
    constructor(props) {
        super(props);
        
        this.state = {
        data: [],
        loading: false,
        error: null,
        };
    }
    fetchAll = () => {
        const client = new ApolloClient({
  uri: 'http://localhost:4000/',
  cache: new InMemoryCache(),
        });
        const GET_ALL = gql`
        query Query {
  category(input: { title: "all" }) {
    name
    
    products {
      id
      
      name
      inStock
      gallery
      description
      category
      attributes {
        id
        
        name
        type
        items {
          displayValue
          
          value
          id
        }
      }
      prices {
        currency {
          symbol
          
          label
        }
        amount
        
      }
      brand
    }
  }
  currencies {
    label
    
    symbol
  }
} `;
        client.query({ query: GET_ALL }).then((result) => {
            console.log(result);
            this.setState({ data: result.data.category.products, loading: false });
            },
            (error) => {
            this.setState({loading:true,error:error})
        });
    }
     
    componentDidMount() {
        this.setState({ loading: true });
        this.fetchAll();
        }
           
render() {
   const { data, loading, error } = this.state;
    if (error) {
      return <p>Something went wrong</p>;
    }

    if (loading) {
      return <p>Loading ...</p>;
    }
    if(data !== [])
    {
      return (
      <>
      <Header>All products</Header>
      <ContainerProductList>
        {data.map(product =>
          <ProductCardStyle key={product.id}>
            <Image src={product.gallery[0]} alt=" product" />
            <Title>{product.brand} {product.name}</Title>
            <Price>{product.prices.currency.symbol } {product.prices.amount }</Price>
          </ProductCardStyle>
        )}
          </ContainerProductList>
          </>
    );
    } else { this.fetchAll(); return "Loading..."; }
    }
};

export default AllCategories;
