import { PureComponent} from "react";
import styled from "styled-components";
import icon from "./button-icon-empty-cart.png";
import { Link } from "react-router-dom";
import { connect } from 'react-redux';
import { setItemInCart } from "../../store/cart";

const ProductCardStyle = styled.div`

flex: 1;
order: 0;
flex-grow: 0;
background: #FFFFFF;
padding: 16px;
width: 386px;
position: relative;
&:hover {
box-shadow: 0px 4px 35px rgba(168, 172, 176, 0.19);
}
`;
const Text = styled.p`
position: absolute;
left: 25.42%;
right: 25.71%;
top: 150px;
display: ${props => props.inStock ? `none` : `block`};
font-weight: 400;
font-size: 24px;
line-height: 160%;
color: #8D8F9A;

`;
const Button = styled.button`
display:  none;
background: #5ECE7B;
width: 52px;
height: 52px;
border: none;
border-radius: 50%;
justify-content: center;
${props => props.inStock ? ` opacity: 1 `: `display: none !important; opacity: 0` } ;

${ProductCardStyle}:hover & {
  display: block;
  position: absolute;
  top: 320px;
  left: 300px;
  cursor: pointer;  
}
`;
const Image = styled.img`
width: 354px;
height: 330px;
object-fit: cover;
position: relative;
${props => props.inStock ? `opacity: 1` : `opacity: 0.5`}
`;

const Title = styled.p`
font-weight: 300;
font-size: 18px;
line-height: 160%;
text-align: left;
color: ${props => props.inStock? `#1D1F22` : `#8D8F9A` } ;
margin: 24px 0 0 0;
`;
const Price = styled.p`
margin: 0;
font-weight: 500;
font-size: 18px;
line-height: 160%;
text-align: left;
color: ${props => props.inStock? `#1D1F22` : `#8D8F9A` } ;
`;
class ProductCard extends PureComponent{
constructor(props) {
        super(props);
        
        this.state = {
            product: this.props.product,
            inStock: this.props.product.inStock,
      };
    
    }

    render() {
        const { product, inStock } = this.state;
        return (
            <ProductCardStyle key={product.id} >
                <Image inStock={inStock} src={product.gallery[0]} alt=" product" />
                <Text inStock={inStock}>OUT OF STOCK</Text>
                <Button inStock={inStock}> <img src={icon} alt="icon" onClick={()=> setItemInCart(product)} /></Button>
                <Title inStock={inStock}>{product.brand} {product.name}</Title>
                <Price inStock={inStock}>{product.prices[0].currency.symbol} {product.prices[0].amount}</Price>
                {inStock && <Link
                style={{ position: "absolute", inset: 0 }}
                to={`${product.id}`} ></Link>}
            </ProductCardStyle>
        )
    }
  
}
const mapStateToProps = state => ({
    itemsInCart: state.itemsInCart
});
const mapDispatchToProps = dispatch => ({ 
    setItemInCart: item => dispatch(setItemInCart(item))
});
export default connect(mapStateToProps, mapDispatchToProps())(ProductCard);
