import { PureComponent } from "react";
import { useParams } from "react-router-dom";
import { Interweave } from 'interweave';
import getProductById from "../other-components-and-functions/getProductById";
import styled from "styled-components";
import "./ProductPage.css";


const FilterColor = styled.div`
width: 32px;
height: 32px;
cursor: pointer;
background-color: ${props => props.color};
border: ${props => props.selected ? `1px solid #5ECE7B` : `none`};
`;

function withParams(Component) {
  return props => <Component {...props} params={useParams()} />;
}

class ProductPage extends PureComponent {
    constructor(props) {
        super(props);
        this.state = {
            product: [],
            loading: false,
            error: null,
            amount: null,
        };
        this.getPrice = this.getPrice.bind(this);
    } 
    getPrice() {
        this.state.product.prices.map((item) => {
            if (item.currency.symbol === this.state.currency) {
                return this.setState({ amount: item.amount });
            } else { return null; }
        })
    }
    async fetchData(props) {
      
    let productsRequest = await getProductById(this.props);
    return  this.setState({ product: productsRequest, loading: false });
    }
   componentDidMount() {
    let { id } = this.props.params;
    console.log(id);
    this.setState({ loading: true });
    this.fetchData(id);
    }
    componentDidUpdate(props, state) {
     if (this.state.currency !== this.props.currency) {
       this.setState({ currency: this.props.currency });
       this.getPrice();
    }
  }
    
    render() {
        const { product, loading, error, amount } = this.state;
        console.log(product);
        let gallery =product.gallery;
        console.log(gallery);
        console.log(product.attribute);
        if (error) {
            return <p>Something went wrong</p>;
        }

        if (loading) {
            return <p>Loading ...</p>;
        }
        if (product !== null) {
            return (
                
                <div className="product-container">
                    <div className="small-image-wrapper">
                        {/* {product.gallery.map((img, index) => {
                            return <img src={img} key={index} alt="prod"/>
                        })} */}
                    <img className="small-image" src={product.gallery} alt="" />
                    </div>
                    <img className="big-image" src={product.gallery} alt=""/>
                    <div className="content-block">
                        <p className="brand">{product.brand}</p>
                        <p className="name">{product.name} </p>
                        
                        {/* {product.attributes.map((item, index) => {
                            if (item.name !== "Color") {
                                return (
                                    <>
                                        <p key={index} className="attribute-name">{item.name }</p>
                                    </>
                                )
                            } else {
                                return null;
                                
                            }
                        })} */}
                       
                        <p className="attribute-name">Size:</p>
                        <div className="attribute-item-container">
                            <div className="attribute-item"><p>S</p></div>
                            <div className="attribute-item-selected"><p>M</p></div>
                            <div className="attribute-item"><p>L</p></div>
                            <div className="attribute-item"><p>XL</p></div>
                        </div>   
                        <p className="attribute-name">Color:</p>
                        <div className="attribute-color-container">
                            <FilterColor color="black" selected={false} />
                            <FilterColor color="green" selected={false}/>
                            <FilterColor color="grey" selected={true} />
                        </div> 
                        <p className="attribute-name">Price:</p>
                        <p className="price">$ {amount }</p>
                        <button className="button-style">Add to cart</button>
                        <p className="description"><Interweave content={product.description} /></p>
                    </div>
                </div>
            );
        } else {
            return "Loading...";
        }
    }
}
export default withParams(ProductPage);
