export default function Slider(props) {
    const [slideIndex, setSlideIndex] = useState(0);
    const handleClick = (direction) => {
        if (direction === "left") {
            setSlideIndex(slideIndex>0?slideIndex-1: 0)
        } else {
            setSlideIndex(slideIndex < 2 ? slideIndex + 1 : 0)
        }
    };
    return (
        <Container>
            <Arrow direction="left" onClick={()=>handleClick("left")}>K</Arrow>
            <Wrapper slideIndex={slideIndex} >
                <Slide>
                <ImageContainer>
                        <Image src={props.data[0]}></Image>
                    </ImageContainer>
                </Slide>
                <Slide>
                <ImageContainer>
                        <Image src={props.data[1]}></Image>
                    </ImageContainer>
                </Slide>
                <Slide>
                <ImageContainer>
                        <Image src={props.data[2]}></Image>
                    </ImageContainer>
                </Slide>
            </Wrapper>
            <Arrow direction="right" onClick={()=>handleClick("right")}>K</Arrow>
        </Container>
    )
}
