import { PureComponent } from "react";
import { useParams } from "react-router-dom";
import getProductById from "../other-components-and-functions/getProductById";
import styled from "styled-components";

const Container = styled.div`
margin: 80px 100px;
display:flex;
flex-wrap: wrap;
flex-direction: row;
`;

const SmallImageWrapper = styled.div`
display: flex;
flex-direction: column;
flex-wrap: wrap;
margin: 0px 40px 40px 0px;
`;
const SmallImage = styled.div`
width: 80px;
height: 80px;

`;


function withParams(Component) {
  return props => <Component {...props} params={useParams()} />;
}

class ProductPage extends PureComponent {
    constructor(props) {
        super(props);
        this.state = {
            product: [],
            loading: false,
            error: null,
        };
    } 
   componentDidMount() {
    let { id } = this.props.params;
    console.log(id);
    this.setState({ loading: true });
    this.fetchData(id);
    }
  async fetchData(props) {
    let productsRequest = await getProductById(this.props);
    return  this.setState({ product: productsRequest, loading: false });
}
    render() {
        const { product, loading, error } = this.state;
        if (error) {
            return <p>Something went wrong</p>;
        }

        if (loading) {
            return <p>Loading ...</p>;
        }
        if (product !== null) {
            return (
                <div className="product-container"></div>
            );
        } else {
            return "Loading...";
        }
    }
}
export default withParams(ProductPage);
