import { PureComponent } from "react";
import { connect } from 'react-redux';
import { updateCurrency, fetchCurrency } from "../store/currency";
import "../components/header/Header.css";

class CurrencyList extends PureComponent {
    constructor(props) {
        super(props);
        this.state = {
            activeItem: 0,
            currencies: null,
            currency: this.props.currency,
        }
    }
    
    componentDidMount() {
        const response = this.props.dispatch(fetchCurrency());
        this.setState({ currencies: response });
    }
    onSelectItem = (index, name) => {
        this.setState({ activeItem: index, currency: name });
        this.props.dispatch(updateCurrency(name));
    };
    render() {
        if (this.state.currencies !== null) {
            return (
                <div className="currencies">
                    {this.state.currencies.map((item, index) => (
                        <div className={this.state.activeItem === index ? "currency-selected" : ""}
                            onClick={() => this.onSelectItem(index, item)}
                            key={index}> `${item.symbol} ${item.label}` </div>))}
                
            </div>
        )
        } else { return "Loading..." }
    }
}
function mapStateToProps(state) {
    return {
        currency: state.currency.currency,
        currencies: state.currency.currencies,
    }
};
export default connect(mapStateToProps)(CurrencyList);
