import { PureComponent } from "react";
import "./Header.css";
import logo from "./logo-shop.png";
import { NavLink, Outlet } from "react-router-dom";

const linkStyle = {
    textDecoration: "none",
    color: "black"
} 
const active = {
     color: "#5ECE7B",
    borderBottomStyle: "solid",
    borderColor: "#5ECE7B",
  cursor: "default",
  textDecoration: "none"
}
class Header extends PureComponent {
    render() {
        return (
            <> 
                <div className="navbar-container">
                <div className="navbar-row">
                    <div className="navbar-menu"> 
                            <NavLink to={"all" || "/"} style={({ isActive }) =>isActive ? active : linkStyle }>All</NavLink>
                        <NavLink to="clothes" style={({ isActive }) =>isActive ? active : linkStyle } >Clothes </NavLink>
                        <NavLink to="tech" style={({ isActive }) =>isActive ? active : linkStyle } >Tech </NavLink>
                    </div>
                    
                       
                   <div> 
                        <img className="navbar-icon" src={logo} alt="logo" /> 
                    </div>
                    <div className="navbar-bag"> Bag   </div>
                </div>
                </div>
                <main className="container-main">
                <Outlet />
                </main>
            </>)
    }
};
export default Header;
